import React from 'react';
import ReactDOM from 'react-dom';

class Modal extends React.Component {
	constructor(props) {
		super(props);
	}

	render() {
		return ReactDOM.createPortal(
			<div>{this.props.content}</div>,
			document.querySelector('#modal')
		);
	}
}

export default Modal;
