import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import _ from 'lodash';

import { getJournals } from '../../../actions';
import ContentDiv from '../../shared/ContentDiv';
import { Title, JournalListDiv, JournalDiv, CreateButton } from './styles';
import JournalCreate from '../JournalCreate';

class JournalList extends React.Component {
	state = { createModalOpen: false };

	constructor(props) {
		super(props);
		this.setModalRef = (element) => {
			this.modalRef = element;
		};
	}

	componentDidMount() {
		this.props.getJournals();
	}

	renderJournalList() {
		const renderedJournals = [];
		_.forEach(this.props.journals, (journal) => {
			renderedJournals.push(
				<JournalDiv
					as={Link}
					to={`/journals/${journal._id}`}
					key={journal._id}
				>
					<h3>{journal.title}</h3>
					<div>{journal.date}</div>
				</JournalDiv>
			);
		});

		return renderedJournals;
	}

	onCreateClick = () => {
		this.setState({ createModalOpen: !this.state.createModalOpen });
	};

	render() {
		return (
			<ContentDiv>
				<Title>Journal List</Title>
				<br />
				<CreateButton onClick={this.onCreateClick} modalRef>
					Create
				</CreateButton>
				<JournalCreate isOpen={this.state.createModalOpen} />
				<JournalListDiv>{this.renderJournalList()}</JournalListDiv>
			</ContentDiv>
		);
	}
}

const mapStateToProps = (state) => {
	return { journals: state.journals };
};

export default connect(mapStateToProps, { getJournals })(JournalList);
