import styled, { keyframes } from 'styled-components';
import ContentDiv from '../../shared/ContentDiv';
import colors from '../../shared/colors';
import { Button } from '../../shared/Button';

const scale = keyframes`
	from {
		width: 50%;
		height: 50%;
	}
	to {
		width: 75%;
		height: 75%;
	}
`;

export const Title = styled.h1`
	margin: 0;
	margin-bottom: 10px;
	width: 100%;
	text-align: center;
`;

export const JournalTitle = styled.h4`
	margin-top: 5px;
	margin-bottom: 5px;
`;

export const JournalListDiv = styled.div`
	display: grid;
	grid-template-columns: repeat(auto-fit, 200px);
	grid-auto-rows: 200px;
	justify-content: center;
	column-gap: 10px;
	row-gap: 10px;
	flex-grow: 1;
	margin-top: 10px;
	padding-bottom: 10px;
`;

export const EmptyJournalDiv = styled.div`
	background-color: ${colors.darkerWhite};
	border-radius: 10px;
	grid-column-start: span 1;
	grid-row-start: span 1;
	display: flex;
	flex-direction: column;
	border: 2px dashed ${colors.black};
	justify-content: center;
	width: 50%;
	height: 50%;
	justify-self: center;
	align-self: center;
	box-shadow: 0px 0px 0px 0px;

	transition: background-color .5s, box-shadow .25s;
	&:hover {
		background-color: ${colors.white};
		box-shadow: 0px 2px 4px 2px ${colors.blackShadow};
		animation: ${scale} .5s linear 1;
	}
`;

export const JournalDiv = styled.div`
	background-color: ${colors.white};
	border-radius: 4px;
	grid-column-start: span 1;
	grid-row-start: span 1;
	box-shadow: 0px 2px 4px 2px ${colors.blackShadow};

	display: flex;
	text-decoration: none;
	flex-direction: column;
	padding-bottom: 10px;
	color: ${colors.black};

	transition: background-color .2s, box-shadow .2s, filter .5s;
	&:hover {
		box-shadow: 0px 2px 4px 4px ${colors.blackShadow};
		& > div {
			transition: background-color .2s;
			background-color: ${colors.brightBlue};
		}

		button {
			opacity: 1.0;
		}
	}
`;

export const DeleteButton = styled(Button)`
	align-self: flex-end;
	border: none;
	background: none;
	margin-right: 10px;
	height: 2em;
	width: fit-content;
	font-size: regular;
	box-shadow: none;
	color: ${colors.red};
	pointer-events: auto;
	opacity: 0.0;

	transition: background-color .2s, opacity .5s;
	&:hover {
		background-color: ${colors.red};
		color: ${colors.black};
	}
`;

export const ViewButton = styled(Button)`
	align-self: center;
	margin: 0;
	border: none;
	background: none;
	height: 2em;
	width: fit-content;
	font-size: regular;
	box-shadow: none;
	color: ${colors.brightBlue};
	opacity: 0.0;
	cursor: pointer; 
	transition: background-color .2s, opacity .5s;
`;

export const JournalLinkDiv = styled.div`
	border-radius: 10px;
	height: 100%;
	text-align: center;
	display: flex;
	flex-direction: column;
	justify-content: center;
	text-decoration: none;
	color: ${colors.black};
	pointer-events: auto;
	align-self: center;
	width: 80%;
`;

export const CreateButton = styled(Button)`
	align-self: center;
	background-color: ${colors.brightBlue};
	color: ${colors.white};
	box-shadow: 0px 2px 2px 1px ${colors.blackShadow};
	border: none;

	transition: box-shadow .2s;
	&:hover {
		box-shadow: 0px 2px 2px 2px ${colors.blackShadow};
	}
`;

export const BannerDiv = styled.div`
	background-color: ${colors.blue};
	align-self: center;
	margin-top: 5px;
	width: 95%;
	height: 10%;
	border-radius: 4px;
	transition: background-color .2s;
`;
