import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';
import Quill from 'quill';
import ContentDiv from '../shared/ContentDiv';
import { Button } from '../shared/Button';
import { getJournal } from '../../actions';
import colors from '../shared/colors';
import JournalToolbar from './JournalToolbar';

const JournalDiv = styled.div`
	height 80%; 
	width 100%;
	align-self: center;
`;
const EditorDiv = styled.div`
	width: 100%;
	height: 100%;
	box-shadow: 4px 4px 8px 2px ${colors.blackShadow};
`;

const PostButton = styled(Button)`
	background-color: ${colors.green};
	height: auto;
	margin-top: 20px;
`;

class JournalShow extends React.Component {
	constructor(props) {
		super(props);
		this.editor = null;
	}

	componentDidMount() {
		this.props.getJournal(this.props.match.params.id);
	}

	componentDidUpdate() {
		this.addEditor();
	}

	addEditor() {
		if (!this.editor) {
			this.editorRoot = document.querySelector('#editor');
			const options = {
				modules     : {
					toolbar : [ (toolbar: '#toolbar') ]
				},
				debug       : 'info',
				placeholder : 'Compose an epic...',
				readOnly    : false,
				theme       : 'snow',
				bounds      : this.editorRoot
			};
			this.editor = new Quill(this.editorRoot, options);
			const date = new Date();
			this.editor.setText(
				`${date.toLocaleDateString()}\n${date.toLocaleTimeString()}`
			);

			this.editor.formatLine(1, 2, 'bold');
			this.editor.focus();
		}
	}

	onPostClick() {}

	render() {
		if (!this.props.journal) {
			return null;
		}

		return (
			<ContentDiv>
				<JournalDiv>
					{this.props.journal.title}

					<EditorDiv id="editor" />
					<PostButton onClick={this.onPostClick}>Post</PostButton>
				</JournalDiv>
			</ContentDiv>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { journal: state.journals[ownProps.match.params.id] };
};

export default connect(mapStateToProps, { getJournal })(JournalShow);
