import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';
import Quill from 'quill';
import { BaseContentDiv } from '../shared/ContentDiv';
import { Button } from '../shared/Button';
import { getJournal, updateJournal } from '../../actions';
import colors from '../shared/colors';
import editor from '../../apis/editor';

const GridDiv = styled(BaseContentDiv)`
	display: grid;
	grid-template-columns: 20% 80%;
`;

const JournalDiv = styled.div`
	height 80%; 
	width 100%;
	align-self: start;
`;
const EditorDiv = styled.div`
	width: 100%;
	height: 100%;
	box-shadow: 4px 4px 8px 2px ${colors.blackShadow};
	border-radius: 6px;
`;

const PostButton = styled(Button)`
	background-color: ${colors.green};
	height: auto;
	margin-top: 20px;
`;

const EntriesDiv = styled.div`height: 100%; width`;

class JournalShow extends React.Component {
	constructor(props) {
		super(props);
		this.editor = null;
	}

	componentDidMount() {
		this.props.getJournal(this.props.match.params.id);
	}

	componentDidUpdate() {
		this.addEditor();
	}

	addEditor() {
		if (!this.editor) {
			this.editorRoot = document.querySelector('#editor');
			var toolbarOptions = [
				[ 'bold', 'italic', 'underline', 'strike' ], // toggled buttons
				[ 'blockquote', 'code-block' ],

				[ { header: 1 }, { header: 2 } ], // custom button values
				[ { list: 'ordered' }, { list: 'bullet' } ],
				[ { script: 'sub' }, { script: 'super' } ], // superscript/subscript
				[ { indent: '-1' }, { indent: '+1' } ], // outdent/indent
				[ { direction: 'rtl' } ], // text direction

				[ { size: [ 'small', false, 'large', 'huge' ] } ], // custom dropdown
				[ { header: [ 1, 2, 3, 4, 5, 6, false ] } ],

				[ { color: [] }, { background: [] } ], // dropdown with defaults from theme
				[ { font: [] } ],
				[ { align: [] } ],
				[ 'clean' ] // remove formatting button
			];
			const options = {
				modules     : {
					toolbar : toolbarOptions
				},
				debug       : 'info',
				placeholder : 'Compose an epic...',
				readOnly    : false,
				bounds      : this.editorRoot,
				theme       : 'snow'
			};
			this.editor = new Quill(this.editorRoot, options);
			const date = new Date();
			this.editor.setText(
				`${this.props.journal
					.title}\n${date.toLocaleDateString()}\n${date.toLocaleTimeString()}`
			);
		}
	}

	onPostClick = () => {
		const { journal } = this.props;
		this.props.updateJournal(journal._id, {
			entries : [
				...journal.entries,
				{ content: this.editor.getContents() }
			]
		});
	};

	renderEntries() {
		this.journal.entries.map((entry) => {
			return <div>- {}</div>;
		});
	}

	render() {
		if (!this.props.journal) {
			return null;
		}

		return (
			<GridDiv>
				<EntriesDiv>
					<h3>Entries</h3>
					{this.renderEntries()}
				</EntriesDiv>
				<JournalDiv>
					<EditorDiv id="editor" />
					<PostButton onClick={this.onPostClick}>Post</PostButton>
				</JournalDiv>
			</GridDiv>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { journal: state.journals[ownProps.match.params.id] };
};

export default connect(mapStateToProps, { getJournal, updateJournal })(
	JournalShow
);
