import React from 'react';
import { connect } from 'react-redux';

import { JournalDiv, PostButton, EditorDiv } from './styles';
import editor from '../../../apis/editor';
import ReactQuill from 'react-quill';
import { getJournal, updateJournal } from '../../../actions';

const toolbarOptions = [
	[ 'bold', 'italic', 'underline', 'strike' ], // toggled buttons
	[ 'blockquote', 'code-block' ],

	[ { header: 1 }, { header: 2 } ], // custom button values
	[ { list: 'ordered' }, { list: 'bullet' } ],
	[ { script: 'sub' }, { script: 'super' } ], // superscript/subscript
	[ { indent: '-1' }, { indent: '+1' } ], // outdent/indent
	[ { direction: 'rtl' } ], // text direction

	[ { size: [ 'small', false, 'large', 'huge' ] } ], // custom dropdown
	[ { header: [ 1, 2, 3, 4, 5, 6, false ] } ],

	[ { color: [] }, { background: [] } ], // dropdown with defaults from theme
	[ { font: [] } ],
	[ { align: [] } ],
	[ 'clean' ] // remove formatting button
];

class EntryShow extends React.Component {
	constructor(props) {
		super(props);
		this.modules = {
			toolbar : toolbarOptions
		};

		this.state = { text: '' };
	}

	componentDidMount() {
		this.setState({ text: this.props.journal.entries[this.props.entryId] });
	}

	handleChange = (value) => {
		console.log(value);
		this.setState({ text: value });
	};

	onPostClick = () => {
		const { journal } = this.props;
		this.props.updateJournal(journal._id, {
			entries : [
				...journal.entries,
				{
					date : new Date().toLocaleString(),
					text : this.state.text
				}
			]
		});
	};

	render() {
		if (!this.props.journal) {
			return null;
		}

		return (
			<JournalDiv>
				<EditorDiv
					theme="snow"
					value={this.state.text}
					onChange={this.handleChange}
					modules={this.modules}
					readOnly={this.props.entryId !== undefined}
					defaultValue={this.state.text}
				/>
				<PostButton onClick={this.onPostClick}>Post</PostButton>
			</JournalDiv>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { journal: state.journals[ownProps.journalId] };
};

export default connect(null, { getJournal, updateJournal })(EntryShow);
