import React from 'react';
import { connect } from 'react-redux';

import { JournalDiv, PostButton } from './styles';
import editor from '../../../apis/editor';
import ReactQuill from 'react-quill';
import { getJournal } from '../../../actions';

class EntryShow extends React.Component {
	state = { text: '' };

	constructor(props) {
		super(props);
	}

	componentDidMount() {
		this.props.getJournal(this.props.journalId);
	}

	handleChange = (value) => {
		this.setState({ text: value });
	};

	render() {
		if (!this.props.journal) {
			return null;
		}

		return (
			<JournalDiv>
				<ReactQuill
					value={this.state.text}
					onChange={this.handleChange}
				/>
				<PostButton onClick={this.onPostClick}>Post</PostButton>
			</JournalDiv>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { journal: state.journals[ownProps.journalId] };
};

export default connect(mapStateToProps, { getJournal })(EntryShow);
