import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`

    html, body, #root {
        margin: 0;
        height: 100vh;
        width: 100vw;
        padding: 0;
        min-width: 800px;
    }

    i {
        padding-right: 3px;
    }



`;
