import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';
import { getJournal } from '../../../actions';

class EntryShow extends React.Component {
	componentDidMount() {
		console.log(this.props.match.params);
		this.props.getJournal(this.props.journalId);
	}

	componentDidUpdate() {
		this.addEditor();
	}

	addEditor() {
		if (!this.editor) {
			this.editorRoot = document.querySelector('#editor');
			if (!this.state.readOnly) {
				this.editor = editor(this.editorRoot);

				this.editor.setText(
					`${this.props.journal
						.title}\n${this.date.toLocaleDateString()}\n${this.date.toLocaleTimeString()}`
				);
			} else {
			}
		}
	}

	render() {
		if (!this.props.journal) {
			return null;
		}

		return (
			<JournalDiv>
				<EditorDiv id="editor" />
				<PostButton onClick={this.onPostClick}>Post</PostButton>
			</JournalDiv>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { journal: state.journals[ownProps.journalId] };
};

export default connect(mapStateToProps, { getJournal })(EntryShow);
