import { CREATE_JOURNAL, GET_JOURNALS } from '../actions/types';
import _ from 'lodash';

export default (state = [], action) => {
	switch (action.type) {
		case GET_JOURNALS:
			return [
				...state,
				_.keyBy(action.payload, (journal) => {
					return journal._id;
				})
			];
		case CREATE_JOURNAL:
			return [ ...state, action.payload ];
		default:
			return state;
	}
};
