import React from 'react';
import { connect } from 'react-redux';

import { signIn, signOut } from '../actions';

class GoogleSignIn extends React.Component {
	componentDidMount() {
		window.gapi.load('auth2', () => {
			window.gapi.auth2
				.init({
					client_id :
						'299170523315-95a9l5qcn65k3oiatcd488kcvgul6dl9.apps.googleusercontent.com',
					scope     : 'email'
				})
				.then(() => {
					this.authInstance = window.gapi.auth2.getAuthInstance();
					console.log(this.authInstance);
				});
		});
	}

	onClick = async () => {
		console.log('click');
		this.authInstance.signIn();
	};

	renderSignInButton() {}

	render() {
		return (
			<button onClick={this.onClick} className="ui button">
				<i className="fab fa-google" /> Sign in
			</button>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	return { user: state.user };
};

export default connect(mapStateToProps, { signIn, signOut })(GoogleSignIn);
